import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import { Authresponse } from '../authresponse';
import { User } from '../user';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  user: User ={
    contactNumber:'',
    password:''
  };

  constructor(private svc :AuthService){}
  ngOnInit(): void {
    console.log("login componenet called")
  }

onLogin(form:any){
  console.log(form);
  this.svc.logIn(form).subscribe((response)=>{
        console.log(response);
        console.log(response.token);
        
        if(response){
          alert("Login sucessfull")
          // window.location.reload();
        }
        else
        {
          alert("Login Failed")
        }
      })
}
}

<div class="container">
  <div class="row">
    <div class="col-sm-9 col-md-7 col-lg-5 mx-auto">
      <div class="card border-0 shadow rounded-3 my-5">
        <div class="card-body p-4 p-sm-5">
          <h5 class="card-title  text-center mb-5  fs-5">Sign In</h5>
          <form  #loginForm="ngForm" (ngSubmit)="onLogin(loginForm.value)">
            <div class="form-floating mb-3">
              <input type="text" class="form-control" id="contactNumber"  name="contactNumber" [(ngModel)]="user.contactNumber">
              <label for="contactNumber">ContactNumber</label>
            </div>
            <div class="form-floating mb-3">
              <input type="password" class="form-control" id="pass" placeholder="Password" name="password" [(ngModel)]="user.password">
              <label for="password">Password</label>
            </div>
            <div class="d-grid">
              <button class="btn btn-success  fw-bold" style="color: inherit; text-decoration:none;display: block;" type="submit" >Sign in</button>
            </div>
          </form>
          </div>
      </div>
    </div>
  </div>
</div>
